package com.assignment.java;

public class forLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=20;
		
		for(int i=1;i<22;i++)
		{
			if(i%2==0)
			{
				System.out.println("print the number of even "+i);
			}
		
		}
		for(int i=1;i<20;i++)
		{
			if(i%2!=0)
			{
				System.out.println("print the odd number "+i);
			}
		
		}
	}

}
